INSERT INTO `speaking` VALUES (1,'2025-05-17 10:28:13.429187',_binary '','2025-06-04 09:42:07.475153','Introduce yourself','http://138.2.91.94:9000/h2t-english/speaking/1-b39e036d-ec69-459f-8b4e-e00a01a05447','Introduce yourself',2,120,'Introduce yourself',NULL,4),(2,'2025-06-01 20:02:48.591116',_binary '','2025-06-04 21:40:02.772637','This lesson explores the development of artificial intelligence, its key milestones, and its impact on society. Students will improve their speaking skills through discussions and interactive exercises.','http://138.2.91.94:9000/h2t-english/speaking/2-bf116667-1ce6-434f-be83-775892562da2','The Evolution of AI',4,120,'How is AI changing education? Discuss its advantages and potential risks.',NULL,9),(3,'2025-06-02 14:19:52.319882',_binary '','2025-06-03 17:52:01.128718','This lesson explores the importance of physical activity for maintaining good health. Students will learn key vocabulary, discuss fitness habits, and practice speaking through interactive exercises.','http://138.2.91.94:9000/h2t-english/speaking/3-0f238dc0-9699-4259-bc64-62cc4753fb2c','Staying Healthy Through Exercise',1,120,'How can busy people stay fit? Share your own habits or suggestions.',NULL,10),(4,'2025-06-02 14:28:07.881193',_binary '','2025-06-02 14:38:30.374635','This lesson teaches students how to borrow books from a library in English. Students will learn key vocabulary, practice dialogues, and discuss library rules.','http://138.2.91.94:9000/h2t-english/speaking/4-8c71c2ea-2573-4582-b010-9e08bef4dc2c','Borrowing Books at the Library',0,120,'Describe your last visit to a library. What did you do there?',NULL,11);
